import React from 'react-native'
import { Image, Text, TouchableOpacity } from 'react-native'
import styles from './style'

export default function CardFilms({ titulo, nota, imagem }) {
    return(

        <TouchableOpacity style={styles.containerFilms}>
            <Image 
            style={styles.imageFilms} 
            source = {require(`../../Img/${imagem}.png`)}
             />

            <Text style={styles.titleFilms} > { titulo } </Text>
            <Text style={styles.titleFilms} > { nota } </Text>
        </TouchableOpacity>

    )
}
