import { StyleSheet } from "react-native";


const styles = StyleSheet.create({
    imageBanner: {
        width: '90%',
        height: 200,
        marginTop: 15,
        borderRadius: 10,
    },
    textBanner: {
        fontSize: 30,
        color: 'white',
        marginTop: 15,
        alignSelf: 'flex-start',    
        marginLeft: 10,
        fontWeight: 'bold'
    }
})


export default styles
