import React from 'react-native'
import { Image, Text, TouchableOpacity } from 'react-native'
import styles from './style'

export default function CardFilms({ title, feedback, image }) {
    return(

        <TouchableOpacity style={styles.containerFilms}>
            <Image style={styles.imageFilms} source={require(`../../img/${props.image}.png}`)} />
            <Text style={styles.titleFilms} > { title } </Text>
            <Text style={styles.titleFilms} > { feedback } </Text>
        </TouchableOpacity>

    )
}
